import Component from '../utils/Component'

interface BlockProps {
    class: string,
    content: Component<any>[] | string
}

class Block extends Component<BlockProps> {
    template(): string {
        return `<div class={{class}}>{{content}}</div>`
    }
}

export default Block

import { EventManager } from './EventManager'
import { Nullable } from '../types/Types'
import Templator from './templator/Templator'

/* global HTMLElement, EventListenerOrEventListenerObject */

enum Event {
    INIT = 'init',
    FLOW_CDM = 'flow:component-did-mount',
    FLOW_RENDER = 'flow:render',
    FLOW_CDU = 'flow:component-did-update'
}

/*
 В отличие от реализации блока в тренажере, решил не использовать _meta.
 Теперь getContent() возвращает не один элемент, а массив элементов.
*/

class Component<T> {
    props: T
    eventManager: EventManager
    _elements: HTMLElement[] = []
    _templator: Templator

    constructor(props: T) {
        this.props = this._makePropsProxy(props)
        this._templator = new Templator(this.template())
        this.eventManager = new EventManager()
        this._registerEvents(this.eventManager)
        this.eventManager.emit(Event.INIT)
    }

    _registerEvents(eventManager: EventManager) {
        eventManager.on(Event.INIT, this.init.bind(this))
        eventManager.on(Event.FLOW_CDM, this._componentDidMount.bind(this))
        eventManager.on(Event.FLOW_RENDER, this._render.bind(this))
        eventManager.on(Event.FLOW_CDU, this._componentDidUpdate.bind(this))
    }

    template(): string {
        return ''
    }

    _createResources() {
        this._elements = this._templator.compile(this.props)
    }

    init() {
        this._createResources()
        this.eventManager.emit(Event.FLOW_CDM)
    }

    _componentDidMount() {
        this.componentDidMount(this.props)
    }

    componentDidMount(_oldProps: T) {}

    _componentDidUpdate(oldProps: T, newProps: T) {
        const isEnabled = this.componentDidUpdate(oldProps, newProps)
        if (isEnabled) {
            this.props = Object.assign(oldProps, newProps)
        }
    }

    componentDidUpdate(oldProps: T, newProps: T) {
        // TODO: заменить на isEqual из тренажера практикума
        return JSON.stringify(oldProps) !== JSON.stringify(newProps)
    }

    setProps = (nextProps: Partial<T>) => {
        if (!nextProps) {
            return
        }
        this.eventManager.emit(Event.FLOW_CDU, this.props, nextProps)
    };

    _render() {
        const newElements = this._templator.compile(this.props)
        this._elements?.forEach((oldNode, index) => {
            if (oldNode) {
                const newNode = newElements[index]
                const parentNode = oldNode.parentNode
                const nextNode = oldNode.nextSibling
                if (parentNode) {
                    oldNode.remove()
                    // если есть следующая нода того же уровня, то инсертим не в конец родителя, а к этой ноде
                    if (nextNode) {
                        parentNode.insertBefore(newNode, nextNode)
                    } else {
                        parentNode.appendChild(newNode)
                    }
                }
            }
        })
        this._elements = newElements
    }

    getContent(): Nullable<HTMLElement[]> {
        return this._elements
    }

    _makePropsProxy(props: any) {
        const self = this
        return new Proxy(props, {
            set(target: any, prorerty: PropertyKey, value: any): boolean {
                target[prorerty] = value
                self.eventManager.emit(Event.FLOW_RENDER)
                return true
            },
            deleteProperty(): boolean {
                throw new Error('Нет доступа')
            }
        })
    }

    addEventListener(listenerName: string, callback: EventListenerOrEventListenerObject): void {
        const contentArr = this.getContent()
        contentArr?.forEach((node) => {
            node.addEventListener(listenerName, callback)
        })
    }

    show(display: 'flex' | 'block'): void {
        const contentArr = this.getContent()
        contentArr?.forEach((node) => {
            node.style.display = display
        })
    }

    hide(): void {
        const contentArr = this.getContent()
        contentArr?.forEach((node) => {
            node.style.display = 'none'
        })
    }
}

export default Component

export class EventManager {
    listeners: Record<string, Function[]>

    constructor() {
        this.listeners = {}
    }

    on(event: string, callback: Function) {
        if (!this.listeners[event]) {
            this.listeners[event] = []
        }

        this.listeners[event].push(callback)
    }

    off(event: string, callback: Function) {
        if (!this.listeners[event]) {
            throw new Error(`Нет события: ${event}`)
        }
        this.listeners[event] = this.listeners[event].filter(
            listener => listener !== callback
        )
    }

    emit(event: string, ...args: any[]) {
        if (!this.listeners[event]) {
            throw new Error(`Нет события: ${event}`)
        }
        this.listeners[event].forEach(function(listener) {
            listener(...args)
        })
    }
}

import TextNodeParser from './TextNodeParser'
import { Context } from '../../types/Types'

/* global HTMLElement */

class Templator {
    _template: string

    constructor(template: string) {
        this._template = template
    }

    compile(context: Context): HTMLElement[] {
        const textNodes = new TextNodeParser(this._template).findAllTextNodes()
        return textNodes.map((textNode) => {
            return textNode.toHTMLElement(context)
        })
    }
}

export default Templator

import TextNode from './TextNode'
import { Nullable } from '../../types/Types'

interface TextNodeData {
    result: Nullable<TextNode>,
    endIndex: Nullable<Number>
}

class TextNodeParser {
    _template: string

    constructor(template: string) {
        this._template = template
    }

    findAllTextNodes(): TextNode[] {
        let template = this._template
        const textNodeArr = []
        let endIndex: Nullable<Number> = 0
        let previousResult: Nullable<TextNode> = new TextNode('', '', undefined, undefined)
        while (previousResult !== null) {
            if (typeof endIndex === 'number') {
                template = template.substring(endIndex).trim()
            }
            const executionResult = this._findTextNode(template)
            endIndex = executionResult.endIndex
            previousResult = executionResult.result
            if (executionResult.result !== null && this._isAvailableTag(executionResult.result.tagName)) {
                textNodeArr.push(executionResult.result)
            }
        }
        if (textNodeArr.length === 0) {
            throw new Error('text todes not found, check your template')
        }
        return textNodeArr
    }

    _isAvailableTag(tagName: string) {
        // запрещены script, style теги (санитайзинг XSS)
        const availableTagNames = ['div', 'button', 'input', 'span',
            'img', 'label', 'h1', 'h2', 'h3', 'h4',
            'h5', 'h6', 'i', 'form', 'datalist', 'option', 'li', 'ul', 'p']
        return availableTagNames.includes(tagName)
    }

    _findTextNode(template: string): TextNodeData {
        if (this._isStartsWithSelfClosingTag(template)) {
            return this._findTextNodeInSelfClosingTag(template)
        } else {
            return this._findTextNodeInUsualTag(template)
        }
    }

    // генерируем TextNode по самозакрывающемуся тэгу (например, <input />)
    _findTextNodeInSelfClosingTag(template: string): TextNodeData {
        const SELF_CLOSING_TAG_REGEXP = /<(\w+)\b[^>]*\/>/g
        const selfClosingTagObject = SELF_CLOSING_TAG_REGEXP.exec(template)
        if (selfClosingTagObject !== null) {
            const TAG_INDEX = 0
            const TAG_NAME_INDEX = 1
            const tag = selfClosingTagObject[TAG_INDEX]
            const tagName = selfClosingTagObject[TAG_NAME_INDEX]
            return {
                result: new TextNode(
                    tag,
                    tagName,
                    undefined,
                    undefined
                ),
                endIndex: tag.length
            }
        }
        return { result: null, endIndex: null }
    }

    // генерируем TextNode по тэгу, который закрывается и открывается (например, <div></div>)
    _findTextNodeInUsualTag(template: string): TextNodeData {
        const USUAL_TAG_REGEXP = /<(\w+)\b[^>]*>/g
        const openingTagObject = USUAL_TAG_REGEXP.exec(template)
        if (openingTagObject == null) {
            return { result: null, endIndex: null }
        }
        const TAG_INDEX = 0
        const TAG_NAME_INDEX = 1
        const startIndex = openingTagObject.index
        const openingTag = openingTagObject[TAG_INDEX]
        const openingTagName = openingTagObject[TAG_NAME_INDEX]

        const TAG_REGEXP = new RegExp(`<[/]?(${openingTagName})+\\b[^>]*>`, 'g')

        let endIndex = null
        let result = null
        let counter = -1
        let resultString = null

        // поиск закрывающего тэга на том же уровне вложенности, что и открывающий
        while ((result = TAG_REGEXP.exec(template))) {
            const TAG_INDEX = 0
            const tag = result[TAG_INDEX]
            if (this._isOpeningTag(tag)) {
                counter++
            } else if (this._isClosingTag(tag) && counter > 0) {
                counter--
            } else if (this._isClosingTag(tag) && counter === 0) {
                endIndex = result.index + tag.length
                resultString = template.substring(startIndex, endIndex).trim()
                // entrails - это все, что находится между открывающим и закрывающим тэгом
                const entrails = resultString.substring(openingTag.length, resultString.length - tag.length)
                result = this._generateTextNode(openingTag, openingTagName, entrails)
                break
            }
        }
        // возвращаем TextNode и индекс элемента шаблонной строки, на котором закончился поиск
        return { result: result, endIndex: endIndex }
    }

    _isStartsWithSelfClosingTag(template: string) {
        const SELF_CLOSING_TAG_REGEXP = /<(\w+)\b[^>]*\/>/g
        const result = SELF_CLOSING_TAG_REGEXP.exec(template.trim())
        return !!result && result.index === 0
    }

    _isOpeningTag(tag: string):boolean {
        const OPENING_TAG_REGEXP = /<\w+\b[^/>]*>/g
        return !!OPENING_TAG_REGEXP.exec(tag)
    }

    _isClosingTag(tag: string):boolean {
        const CLOSING_TAG_REGEXP = /<\/\w+\b[^>]*>/g
        return !!CLOSING_TAG_REGEXP.exec(tag)
    }

    _isParsable(template: string):boolean {
        return this._isOpeningTag(template) || this._isStartsWithSelfClosingTag(template)
    }

    _generateTextNode(openingTag: string, tagName: string, entrails: string): TextNode {
        if (this._isParsable(entrails)) {
            return new TextNode(
                openingTag,
                tagName,
                new TextNodeParser(entrails).findAllTextNodes(),
                undefined
            )
        } else {
            return new TextNode(
                openingTag,
                tagName,
                undefined,
                entrails
            )
        }
    }
}

export default TextNodeParser

import { Nullable, Context, EventData } from '../../types/Types'
import Component from '../Component'
/* global HTMLElement, EventListenerOrEventListenerObject */

class TextNode {
    openingTag: string
    tagName: string
    children?: TextNode[]
    textContent?: string

    constructor(openingTag: string, tagName: string, children?: TextNode[], textContent?: string) {
        this.openingTag = openingTag
        this.tagName = tagName
        this.children = children
        this.textContent = textContent
    }

    toHTMLElement(context: Context):HTMLElement {
        const element = document.createElement(this.tagName)
        this._setClassName(element, context)
        this._setAttributes(element, context)
        this._addEventListener(element, context)
        this._appendChildren(element, context)
        this._handleTextContentParsing(element, context)
        return element
    }

    // шаблонизатор не умеет работать с innerHTML (XSS санитайзинг)
    _handleTextContentParsing(target: HTMLElement, context: Context) {
        if (this.textContent) {
            if (this._isParsable(this.textContent)) {
                const TEMPLATOR_ATTRIBUTE_REGEXP = /\{\{(.*?)}}/gi
                const pathObj = TEMPLATOR_ATTRIBUTE_REGEXP.exec(this.textContent)
                if (pathObj) {
                    const PATH_INDEX = 1
                    const path = pathObj[PATH_INDEX]
                    const data = this._getDataFromContext(context, path)
                    if (data === undefined) {
                        throw new Error(`${path} attribute is undefined in context`)
                    }
                    if (Array.isArray(data) && data[0] instanceof Component) {
                        data.forEach((component: Component<Context>) => {
                            component.getContent()!.forEach(node => {
                                target.appendChild(node)
                            })
                        })
                    } else if (data instanceof Component) {
                        data.getContent()!.forEach(node => {
                            target.appendChild(node)
                        })
                    } else if (data instanceof HTMLElement) {
                        target.appendChild(data)
                    } else if (Array.isArray(data) && data[0] instanceof HTMLElement) {
                        data.forEach(node => {
                            target.appendChild(node)
                        })
                    } else if (typeof data === 'string') {
                        target.textContent = data
                    }
                }
            } else {
                target.textContent = this.textContent
            }
        }
    }

    _addEventListener(target: HTMLElement, context: Context): void {
        const availableEvents = ['DOMContentLoaded', 'click', 'focus', 'submit', 'keyup',
            'mousemove', 'mouseup', 'mousedown', 'mouseout', 'mouseover', 'contextmenu', 'change']
        const EVENT_REGEXP = /@event=\{\{(\w+)}}/gi
        const eventObj = EVENT_REGEXP.exec(this.openingTag!)
        if (eventObj) {
            const PATH_INDEX = 1
            const path = eventObj[PATH_INDEX]
            const data: EventData = this._getDataFromContext(context, path)
            if (data && data.callback instanceof Function && availableEvents.includes(data.name)) {
                target.addEventListener(data.name, data.callback)
            }
        }
    }

    _appendChildren(target: HTMLElement, context: Context): void {
        this.children?.forEach((textNode) => {
            target.appendChild(textNode.toHTMLElement(context)!)
        })
    }

    _isParsable(str: string): boolean {
        const TEMPLATOR_ATTRIBUTE_REGEXP = /\{\{(.*?)}}/gi
        return !!TEMPLATOR_ATTRIBUTE_REGEXP.exec(str)
    }

    _setClassName(target: HTMLElement, context: Context): void {
        let classNameArr = this._getClassNameFromContext(context)
        if (!classNameArr) {
            classNameArr = this._getClassNameFromOpeningTag()
        }
        if (classNameArr) {
            target.classList.add(...classNameArr)
        }
    }

    _getClassNameFromContext(context: Context): Nullable<string[]> {
        const CLASS_AS_TEMPLATOR_ATTRIBUTE_REGEXP = /class=\{\{(.*?)}}/gi
        const classNameObj = CLASS_AS_TEMPLATOR_ATTRIBUTE_REGEXP.exec(this.openingTag!)
        if (classNameObj) {
            const PATH_INDEX = 1
            const path = classNameObj[PATH_INDEX]
            const classNameStr: string = this._getDataFromContext(context, path)
            if (classNameStr) {
                return classNameStr.split(' ')
            }
        }
        return null
    }

    _getClassNameFromOpeningTag(): Nullable<string[]> {
        const CLASS_AS_STRING_REGEXP = /class=["'](.*?)["']/g
        const simpleStringClassNameObj = CLASS_AS_STRING_REGEXP.exec(this.openingTag!)
        if (simpleStringClassNameObj) {
            const CLASS_NAME_INDEX = 1
            const classNameStr = simpleStringClassNameObj[CLASS_NAME_INDEX]
            if (classNameStr) {
                return classNameStr.split(' ')
            }
        }
        return null
    }

    _setAttributes(target: HTMLElement, context: Context): void {
        const ATTRIBUTES_REGEXP = /(\w+)=\{\{(.*?)}}/gi
        let result = null
        while ((result = ATTRIBUTES_REGEXP.exec(this.openingTag!))) {
            const ATTR_INDEX = 1
            const PATH_INDEX = 2
            const attrName = result[ATTR_INDEX]
            const attrPath = result[PATH_INDEX]
            if (this._isAttributeAvailable(attrName)) {
                const attrValue = this._getDataFromContext(context, attrPath)
                if (attrValue) {
                    target.setAttribute(attrName, attrValue)
                }
            }
        }
    }

    _isAttributeAvailable(attr: string) {
        // запрещены onclick, onerror и прочие ивент-атрибуты (санитайзинг XSS)
        const availableAttributes = ['autocomplete', 'type', 'name', 'placeholder', 'id', 'form', 'value', 'src', 'for', 'list']
        return availableAttributes.includes(attr)
    }

    _getDataFromContext(context: Context, path: string) {
        const keys = path.split('.')
        const firstKey = keys[0]
        return keys.slice(1).reduce((acc, key) => (acc = acc[key]), context[firstKey])
    }
}
