import Route from './Route'
import Component from '../Component'
import { Nullable } from '../../types/Types'

/* global History, Event, Window */

class Router {
    routes: Route[]
    _rootQuery: string
    history: History
    static __instance: Nullable<Router>
    _currentRoute: Nullable<Route>
    _notFoundPage?: Component<any>

    constructor(rootQuery: string) {
        if (Router.__instance) {
            return Router.__instance
        }
        this.routes = []
        this.history = window.history
        this._currentRoute = null
        this._rootQuery = rootQuery
        Router.__instance = this
    }

    use(pathname: string, block: Component<any>) {
        const route = new Route(pathname, block, this._rootQuery)
        this.routes.push(route)
        return this
    }

    useNotFound(notFoundPage: Component<any>) {
        this._notFoundPage = notFoundPage
        return this
    }

    start() {
        window.onpopstate = (event: Event) => {
            this._onRoute((event.currentTarget as Window).location.pathname)
        }
        this._onRoute(window.location.pathname)
    }

    _onRoute(pathname: string) {
        let route = this._getRoute(pathname)
        if (!route) {
            if (this._notFoundPage) {
                route = new Route(pathname, this._notFoundPage, this._rootQuery)
            } else {
                return
            }
        }
        if (this._currentRoute && this._currentRoute !== route) {
            this._currentRoute.leave()
        }
        this._currentRoute = route
        route.render()
    }

    go(pathname: string) {
        this.history.pushState({}, '', pathname)
        this._onRoute(pathname)
    }

    back() {
        this.history.back()
    }

    forward() {
        this.history.forward()
    }

    _getRoute(pathname: string) {
        return this.routes.find(route => route.match(pathname))
    }

    destruct() {
        Router.__instance = null
    }
}

export default Router

import Component from '../Component'
import { render } from '../renderDom'

class Route {
    _pathname: string
    _block: Component<any>
    _rootQuery: string

    constructor(pathname: string, _block: Component<any>, _rootQuery: string) {
        this._pathname = pathname
        this._rootQuery = _rootQuery
        this._block = _block
    }

    leave() {
        if (this._block) {
            this._block.getContent()?.forEach((element) => {
                element.remove()
            })
        }
    }

    match(pathname: string) {
        return pathname === this._pathname
    }

    render() {
        render(this._rootQuery, this._block)
    }
}

export default Route

import Component from './Component'
import { Context } from '../types/Types'
/* global HTMLElement */

export default function renderDom(block: Component<Context>): void {
    const root = document.createElement('div')
    const content = block.getContent()
    if (root !== null && content !== null) {
        content.forEach((node) => {
            if (node) {
                root.appendChild(node)
            }
        })
    }
    document.body.appendChild(root)
}

export function render(query: string, block: Component<Context>): void {
    const root = document.querySelector(query)
    const content = block.getContent()
    if (root !== null && content !== null) {
        content.forEach((node) => {
            if (node) {
                root.appendChild(node)
            }
        })
        document.body.appendChild(root)
    }
}
